CREATE   PROCEDURE [dbo].[USP_BBW_GET_ONE_BBBEWREG_BBALCTST]
@IdBewReg int

AS

SET NOCOUNT OFF;
	/* 1. Algemene gegevens ophalen */
	exec USP_BBW_GET_ONE_BBBEWREG_ALGGEG
		@IdBewReg = @IdBewReg

