AND (NOT (BBAFD.KRT_AFD LIKE '%AFG%')) 
AND (NOT (BBAFD.KRT_AFD LIKE '%VERD%')) 
AND (NOT (BBAFD.KRT_AFD LIKE '%VERV%')) 
AND BBINTVBR.ID_TY_INTV = 1

GROUP BY SCF_BR_RD_INTV, KRT_AFD
ORDER BY KRT_AFD, SCF_BR_RD_INTV
